con:config.script:
import java.io.File;
import java.io.FileOutputStream;
import java.io.FileInputStream;
import java.io.InputStream;
import java.io.OutputStream;
import java.io.IOException;
import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.ArrayList;
import java.util.List;
import java.util.zip.ZipEntry;
import java.util.zip.ZipOutputStream;
import java.util.zip.ZipException;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerException;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMSource;
import javax.xml.transform.stream.StreamResult;


import org.w3c.dom.Document;
import org.w3c.dom.Node;
import org.xml.sax.SAXException;


class MetsModificator{

	def log
	def context
	def testRunner

	def MetsModificator(logIn, contextIn, testRunnerIn){
		this.log = logIn
		this.context = contextIn
		this.testRunner = testRunnerIn
	}

	def execute(xmlFilePath, tagnameNodeToModify, zipFilePath,folderToZip ){
		log.info("Executing modification on execute method")		
		log.info("created")		
		DocumentBuilderFactory docFactory = DocumentBuilderFactory.newInstance();
		DocumentBuilder docBuilder = docFactory.newDocumentBuilder();

		Document doc = docBuilder.parse(new File(xmlFilePath));	

		Date now = new Date(System.currentTimeMillis());		
		SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SSS");
		String formattedDate = sdf.format(now);		

		Node node = doc.getElementsByTagName(tagnameNodeToModify).item(0);

		node.setTextContent(formattedDate);	

		log.info("modified md " + tagnameNodeToModify + ": " + formattedDate)

		TransformerFactory transformerFactory = TransformerFactory.newInstance();		
		Transformer transformer = transformerFactory.newTransformer();		
		DOMSource source = new DOMSource(doc);		
		StreamResult result = new StreamResult(new File(xmlFilePath));		
		transformer.transform(source, result);				

		log.info("Modified mets.xml")

		ZipUtil zipUtil = new ZipUtil(zipFilePath, folderToZip);
		zipUtil.zipIt();
		log.info("Finished zipped " + zipFilePath);

	}

	public class ZipUtil
	{
	    List<String> fileList;
	    private String OUTPUT_ZIP_FILE;
	    private String SOURCE_FOLDER;

	    public ZipUtil(String OUTPUT_ZIP_FILE, String SOURCE_FOLDER){
	    	this.OUTPUT_ZIP_FILE = OUTPUT_ZIP_FILE;  
	    	this.SOURCE_FOLDER = SOURCE_FOLDER;
	    	fileList = new ArrayList<String>();
	    	generateFileList(new File(SOURCE_FOLDER));    	
	    }

	    public void zipIt(){	
	     byte[] buffer = new byte[1024];	    	
	     try{	    		
	    	FileOutputStream fos = new FileOutputStream(OUTPUT_ZIP_FILE);
	    	ZipOutputStream zos = new ZipOutputStream(fos);	    		
	    	System.out.println("Output to Zip : " + OUTPUT_ZIP_FILE);

	    	for(String file : this.fileList){	    			
	    		System.out.println("File Added : " + file);
	    		ZipEntry ze= new ZipEntry(file);
	        	zos.putNextEntry(ze);	               
	        	FileInputStream fis = 
	                       new FileInputStream(SOURCE_FOLDER + File.separator + file);	       	   
	        	int len;
	        	while ((len = fis.read(buffer)) > 0) {
	        		zos.write(buffer, 0, len);
	        	}	               
	        	fis.close();
	    	}	    		
	    	zos.closeEntry();
	    	//remember close it
	    	zos.close();	          
	    	System.out.println("Done");
	    }catch(IOException ex){
	       ex.printStackTrace();   
	    }
	   }	    	    

	    private void generateFileList(File node){

	    	//add file only
		if(node.isFile()){
			fileList.add(generateZipEntry(node.getAbsoluteFile().toString()));
		}			
		if(node.isDirectory()){
			String[] subNote = node.list();
			for(String filename : subNote){
				generateFileList(new File(node, filename));
			}
		}	 
	    }		  
	    private String generateZipEntry(String file){
	    	return file.substring(SOURCE_FOLDER.length()+1, file.length());
	    }
	}
}

context.setProperty("MetsModificator", new MetsModificator(log, context, testRunner))
log.info("Added instance on MetsModificator to context")

def xmlFilePath = context.expand('${projectDir}') + "/PIT_expedient_XAdES_detached/mets.xml"
def zipFilePath = context.expand('${projectDir}') + "/PIT_expedient_XAdES_detached.zip"
def folderToZip = context.expand('${projectDir}') + "/PIT_expedient_XAdES_detached"
def tagnameNodeToModify = "voc:numero_expedient"

// get a reference to the library TestSuite
library = testRunner.testCase.testSuite.project.testSuites["Library"]

// find the module within the library 
module = library.testCases["reusable"].testSteps["MetsModificator"]

// initialise the library; which places an instance of Example in the context
module.run(testRunner, context)

// get the instance of example from the context.
def myMets = context.getProperty("MetsModificator")

// run the method, with parameter
myMets.execute(xmlFilePath, tagnameNodeToModify, zipFilePath, folderToZip)
log.info "Executing mymets.execute()"

